actions.script:
const collision = runtime.objects.MovingEnemyCollision
	.getFirstPickedInstance();
const checkpoint = runtime.objects.MovingEnemyCheckpoint
	.getFirstPickedInstance();

const id = checkpoint.instVars.id;
const valid = collision.instVars.validIds
	.split("|").indexOf(id.toString());

if (valid > -1) {
	collision.instVars.direction = 
		checkpoint.instVars.direction;
}

const {
	level,
	username,
	actionCount,
	codeBlockCount,
	levelTimer,
} = runtime.globalVars;

try {
	await leaderboard.addToLeaderboard(
		level,
		username,
		actionCount,
		codeBlockCount,
		Math.round(levelTimer * 1000, 3),
	);

	runtime.callFunction("AddToLeaderboardSuccess")
} catch(error) {
	console.error(error);

	localVars.isError = true;
	runtime.callFunction("AddToLeaderboardError")
}

if (!runtime.globalVars.variableNames.includes("health")) {
	return;
}

const player = runtime.objects.Player.getFirstPickedInstance();

player.instVars.health -= runtime.objects.Damaging
	.getFirstPickedInstance().instVars.damage;

if (player.instVars.health <= 0) {
	state.isError = true;
	runtime.callFunction(
		"ShowError",
		translate("game.error.player-died"),
		2,
	);
}

const block = localVars.byButton ?
	runtime.objects.CodeBlockButton.getFirstPickedInstance()
	: runtime.objects.CodeBlock.getFirstPickedInstance();

const [title, content, exampleTitle, example] = getBlockHelp(block);

localVars.htmlContent = localVars.htmlContent
	.replace("{{title}}", title)
	.replace("{{content}}", content);

if (example !== "") {
	localVars.htmlContent = localVars.htmlContent
		.replace("{{exampleTitle}}", exampleTitle)
		.replace("{{example}}", example)
} else {
	localVars.htmlContent = localVars.htmlContent
		.replace("<p>{{exampleTitle}}</p>", "")
		.replace("<pre class=\"code-example\">{{example}}</pre>", "")
}

const {id: targetId, ids} = localVars;

if (ids === "") {
	runtime.setReturnValue(1);
	return;
}

runtime.setReturnValue(
	ids.split(",").find((id) => parseInt(id) === targetId) ? 1 : 0
);

