actions.script:
const button = runtime.objects.Button.getFirstPickedInstance();

const result = await installGame();

if (result) {
	button.opacity = 0.5;
	button.instVars.isDisabled = true;
}

const {
	leaderboardPage: page,
	leaderboardLevel: level,
	leaderboardSortBy: sortBy,
} = runtime.globalVars;

try {
	const isNotEmpty = await showLeaderboard(
		runtime,
		{
			level,
			page,
			sortBy
		},
	);

	runtime.callFunction("GetLeaderboardSuccess", isNotEmpty);
} catch(error) {
	console.log(error);
	runtime.callFunction("GetLeaderboardError");
}
